// Importar los componentes necesarios
import React from 'react';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Importar React Hook Form, Yup y sus dependencias
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

// Definir el schema de validación usando Yup
const schema = yup.object().shape({
  nombre: yup.string().required('Por favor ingresa tu nombre completo'),
  email: yup
    .string()
    .email('Por favor ingresa un correo electrónico válido')
    .required('Por favor ingresa tu dirección de correo electrónico')
    .matches(/@gmail\.com$/, 'Sólo se aceptan correos de Gmail'),
  telefono: yup
    .string()
    .required('Por favor ingresa tu número de teléfono')
    .matches(/^9\d{9}$/, 'El número debe empezar por 9 y tener 10 dígitos'),
});

// Definir el componente Contacto que muestra el formulario de contacto
function Contacto(props) {
  // Recibir las props con los datos del formulario y la función para manejar el cambio
  const { datos, handleChange } = props;

  // Usar el hook useForm() pasándole el schema de validación
  const { register, handleSubmit, errors } = useForm({
    resolver: yupResolver(schema),
  });

  // Definir una función para manejar el envío del formulario
  const onSubmit = (data) => {
    // Aquí puedes hacer lo que quieras con los datos del formulario
    console.log(data);
  };

  return (
    <div className="Contacto">
      <Container className="mt-4">
        <h1 className="text-center">Contacto</h1>
        <p className="text-center">Ingresa tus datos</p>
      </Container>
      <Container className="mt-4">
        <Row>
          <Col md={{ span: 8, offset: 2 }}>
            {/* Usar el método handleSubmit para manejar el envío del formulario */}
            <Form onSubmit={handleSubmit(onSubmit)}>
              <Form.Group className="mb-3" controlId="nombre">
                <Form.Label>Nombre</Form.Label>
                {/* Pasar el método register al atributo ref del campo de entrada */}
                {/* Usar el valor y el evento onChange de las props para mostrar y modificar los datos del formulario */}
                <Form.Control
                  type="text"
                  placeholder="Ingresa tu nombre completo"
                  name="nombre"
                  value={datos.nombre}
                  onChange={handleChange}
                  ref={register}
                />
                {/* Mostrar el mensaje de error si hay alguno */}
                {errors.nombre && (
                  <span className="text-danger">{errors.nombre.message}</span>
                )}
              </Form.Group>
              <Form.Group className="mb-3" controlId="email">
                <Form.Label>Dirección de correo electrónico</Form.Label>
                {/* Pasar el método register al atributo ref del campo de entrada */}
                {/* Usar el valor y el evento onChange de las props para mostrar y modificar los datos del formulario */}
                <Form.Control
                  type="email"
                  placeholder="Ingresa tu dirección de correo electrónico"
                  name="email"
                  value={datos.email}
                  onChange={handleChange}
                  ref={register}
                />
                {/* Mostrar el mensaje de error si hay alguno */}
                {errors.email && (
                  <span className="text-danger">{errors.email.message}</span>
                )}
              </Form.Group>
              <Form.Group className="mb-3" controlId="telefono">
                <Form.Label>Teléfono</Form.Label>
                {/* Pasar el método register al atributo ref del campo de entrada */}
                {/* Usar el valor y el evento onChange de las props para mostrar y modificar los datos del formulario */}
                <Form.Control
                  type="text"
                  placeholder="Ingresa tu número de teléfono"
                  name="telefono"
                  value={datos.telefono}
                  onChange={handleChange}
                  ref={register}
                />
                {/* Mostrar el mensaje de error si hay alguno */}
                {errors.telefono && (
                  <span className="text-danger">{errors.telefono.message}</span>
                )}
              </Form.Group>
              <br></br>
              <Button variant="primary" type="submit">
                Enviar
              </Button>
              <br></br>
              <br></br>
            </Form>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Contacto;
