import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Form, Button, Alert } from 'react-bootstrap';

function Contacto() {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [comentario, setComentario] = useState('');
  const [emailError, setEmailError] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleInputChange = (e, setter) => {
    setter(e.target.value);
  };

  const verificarCorreo = async () => {
    try {
      const response = await fetch('http://localhost:3001/verificar-correo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      if (response.status === 409) {
        setEmailError('El correo electrónico ya está registrado.');
        return false;
      }
      setEmailError('');
      return true;
    } catch (error) {
      setEmailError('Error al verificar el correo electrónico.');
      return false;
    }
  };

  const enviarDatosAlServidor = async (datos) => {
    try {
      const response = await fetch('http://localhost:3001/guardar-contacto', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datos),
      });
      if (!response.ok) {
        throw new Error('Error al enviar los datos al servidor');
      }
      return await response.json();
    } catch (error) {
      throw error;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setSubmitting(true);
    const emailValido = await verificarCorreo();
    if (!emailValido) {
      setSubmitting(false);
      return;
    }

    try {
      const datos = { nombre, email, telefono, comentario };
      const respuesta = await enviarDatosAlServidor(datos);
      alert(`Formulario enviado con éxito: ${respuesta.mensaje}`);
    } catch (error) {
      alert(`Error al enviar el formulario: ${error.message}`);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      {emailError && <Alert variant="danger">{emailError}</Alert>}
      <Form.Group controlId="formNombre">
        <Form.Label>Nombre</Form.Label>
        <Form.Control
          type="text"
          value={nombre}
          onChange={(e) => handleInputChange(e, setNombre)}
          required
        />
      </Form.Group>

      <Form.Group controlId="formEmail">
        <Form.Label>Email</Form.Label>
        <Form.Control
          type="email"
          value={email}
          onChange={(e) => handleInputChange(e, setEmail)}
          onBlur={verificarCorreo}
          isInvalid={!!emailError}
          required
        />
        <Form.Control.Feedback type="invalid">
          {emailError}
        </Form.Control.Feedback>
      </Form.Group>

      <Form.Group controlId="formTelefono">
        <Form.Label>Teléfono</Form.Label>
        <Form.Control
          type="text"
          value={telefono}
          onChange={(e) => handleInputChange(e, setTelefono)}
          required
        />
      </Form.Group>

      <Form.Group controlId="formComentario">
        <Form.Label>Comentario</Form.Label>
        <Form.Control
          as="textarea"
          rows={3}
          value={comentario}
          onChange={(e) => handleInputChange(e, setComentario)}
          required
        />
      </Form.Group>

      <Button variant="primary" type="submit" disabled={submitting}>
        {submitting ? 'Enviando...' : 'Enviar'}
      </Button>
    </Form>
  );
}

ReactDOM.render(<Contacto />, document.getElementById('root'));


export default Contacto;
