// Importar los componentes necesarios
import React from 'react';
import ReactDOM from 'react-dom';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Definir el componente Contacto que muestra el formulario de contacto
function Contacto() {
  return (
    <div className="Contacto">
      // Crear un contenedor con el título de la página
      <Container className="mt-4">
        <h1 className="text-center">Contáctanos</h1>
        <p className="text-center">Si tienes alguna duda, sugerencia o comentario, por favor llena el siguiente formulario y te responderemos lo antes posible.</p>
      </Container>
      // Crear un contenedor con el formulario de contacto
      <Container className="mt-4">
        <Row>
          <Col md={{ span: 8, offset: 2 }}>
            <Form>
              // Crear un campo para el nombre del usuario
              <Form.Group className="mb-3" controlId="nombre">
                <Form.Label>Nombre</Form.Label>
                <Form.Control type="text" placeholder="Ingresa tu nombre" required />
              </Form.Group>
              // Crear un campo para el email del usuario
              <Form.Group className="mb-3" controlId="email">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Ingresa tu email" required />
              </Form.Group>
              // Crear un campo para el asunto del mensaje
              <Form.Group className="mb-3" controlId="asunto">
                <Form.Label>Asunto</Form.Label>
                <Form.Control type="text" placeholder="Ingresa el asunto de tu mensaje" required />
              </Form.Group>
              // Crear un campo para el mensaje del usuario
              <Form.Group className="mb-3" controlId="mensaje">
                <Form.Label>Mensaje</Form.Label>
                <Form.Control as="textarea" rows={3} placeholder="Ingresa tu mensaje" required />
              </Form.Group>
              // Crear un botón para enviar el formulario
              <Button variant="primary" type="submit">
                Enviar
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

// Renderizar el componente Contacto en el elemento root del HTML
ReactDOM.render(<Contacto />, document.getElementById('root'));
