import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Form, Button, Alert } from 'react-bootstrap';

function Contacto() {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [comentario, setComentario] = useState('');
  const [emailError, setEmailError] = useState('');
  const [submitError, setSubmitError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    // Validaciones adicionales si son necesarias
  }, [nombre, email, telefono, comentario]);

  const verificarCorreo = async (correo) => {
    try {
      const response = await fetch('http://localhost:3001/verificar-correo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: correo }),
      });
      if (!response.ok) {
        throw new Error('Error en la verificación del correo');
      }
      const data = await response.json();
      if (data.mensaje === 'El correo electrónico ya está registrado') {
        setEmailError('El correo electrónico ya está registrado.');
        return false;
      }
      setEmailError('');
      return true;
    } catch (error) {
      setEmailError('No se pudo verificar el correo electrónico.');
      return false;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsSubmitting(true);
    setSubmitError('');

    const esCorreoValido = await verificarCorreo(email);
    if (!esCorreoValido) {
      setIsSubmitting(false);
      return;
    }


};

return (
  <Form onSubmit={handleSubmit}>
    {submitError && <Alert variant="danger">{submitError}</Alert>}
    <Form.Group controlId="formNombre">
      <Form.Label>Nombre</Form.Label>
      <Form.Control
        type="text"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
        required
      />
    </Form.Group>

    <Form.Group controlId="formEmail">
      <Form.Label>Email</Form.Label>
      <Form.Control
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        isInvalid={!!emailError}
        required
      />
      <Form.Control.Feedback type="invalid">
        {emailError}
      </Form.Control.Feedback>
    </Form.Group>

    <Form.Group controlId="formTelefono">
      <Form.Label>Teléfono</Form.Label>
      <Form.Control
        type="text"
        value={telefono}
        onChange={(e) => setTelefono(e.target.value)}
        required
      />
    </Form.Group>

    <Form.Group controlId="formComentario">
      <Form.Label>Comentario</Form.Label>
      <Form.Control
        as="textarea"
        rows={3}
        value={comentario}
        onChange={(e) => setComentario(e.target.value)}
        required
      />
    </Form.Group>

    <Button variant="primary" type="submit" disabled={isSubmitting}>
      {isSubmitting ? 'Enviando...' : 'Enviar'}
    </Button>
  </Form>
);
}

ReactDOM.render(<Contacto />, document.getElementById('root'));

export default Contacto;
